css/admin.css.content:
/* Admin Dashboard - Professional Design */

/* Reset and base styles */
* {
    box-sizing: border-box;
}

body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    background-color: #f5f5f5;
    margin: 0;
    padding: 0;
}

/* Remove all rounded corners */
.card,
.btn,
.form-control,
.alert,
.badge,
.modal-content,
.dropdown-menu,
.nav-tabs .nav-link,
.pagination .page-link {
    border-radius: 0 !important;
}

/* Sidebar */
.sidebar {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    z-index: 100;
    width: 240px;
    background-color: #1a1a1a;
    border-right: 1px solid #333333;
    overflow-y: auto;
}

.sidebar-brand {
    padding: 20px;
    border-bottom: 1px solid #333333;
    background-color: #000000;
}

.sidebar-brand h4 {
    color: #ffffff;
    margin: 0;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.sidebar-nav {
    padding: 0;
    list-style: none;
}

.sidebar .nav-link {
    display: block;
    padding: 15px 20px;
    color: #cccccc;
    text-decoration: none;
    font-weight: 500;
    font-size: 0.9rem;
    border-bottom: 1px solid #333333;
    transition: all 0.2s ease;
}

.sidebar .nav-link:hover {
    background-color: #333333;
    color: #ffffff;
}

.sidebar .nav-link.active {
    background-color: #000000;
    color: #ffffff;
    border-left: 4px solid #007bff;
}

.sidebar .nav-link i {
    width: 20px;
    margin-right: 10px;
    text-align: center;
}

.sidebar-heading {
    padding: 15px 20px 10px;
    font-size: 0.75rem;
    text-transform: uppercase;
    color: #666666;
    font-weight: 600;
    letter-spacing: 0.5px;
}

/* Main content */
.main-content {
    margin-left: 240px;
    padding: 0;
    min-height: 100vh;
    background-color: #f5f5f5;
}

.content-header {
    background-color: #ffffff;
    border-bottom: 1px solid #e5e5e5;
    padding: 20px 30px;
    margin-bottom: 0;
}

.content-header h1 {
    margin: 0;
    font-size: 1.75rem;
    font-weight: 600;
    color: #1a1a1a;
}

.content-header .breadcrumb {
    background: transparent;
    padding: 0;
    margin: 5px 0 0 0;
}

.content-header .breadcrumb-item {
    font-size: 0.85rem;
    color: #666666;
}

.content-body {
    padding: 30px;
}

/* Stats cards */
.stats-card {
    background: #ffffff;
    border: 1px solid #e5e5e5;
    border-left: 4px solid #007bff;
    padding: 20px;
    margin-bottom: 20px;
}

.stats-card.success {
    border-left-color: #28a745;
}

.stats-card.warning {
    border-left-color: #ffc107;
}

.stats-card.danger {
    border-left-color: #dc3545;
}

.stats-card .stats-number {
    font-size: 2rem;
    font-weight: 700;
    color: #1a1a1a;
    margin-bottom: 5px;
}

.stats-card .stats-label {
    color: #666666;
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Form styles */
.form-group {
    margin-bottom: 20px;
}

.form-label {
    font-weight: 600;
    color: #1a1a1a;
    margin-bottom: 8px;
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.form-control {
    border: 2px solid #e5e5e5;
    padding: 12px 15px;
    font-size: 0.9rem;
    background-color: #ffffff;
    transition: border-color 0.2s ease;
}

.form-control:focus {
    border-color: #000000;
    box-shadow: none;
    outline: none;
}

.form-control::placeholder {
    color: #999999;
}

/* Table styles */
.table {
    background-color: #ffffff;
    border: 1px solid #e5e5e5;
    margin-bottom: 0;
}

.table th {
    background-color: #f8f9fa;
    color: #1a1a1a;
    font-weight: 600;
    font-size: 0.85rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    border-bottom: 2px solid #000000;
    border-top: none;
    padding: 15px 12px;
}

.table td {
    vertical-align: middle;
    padding: 12px;
    border-bottom: 1px solid #e5e5e5;
    font-size: 0.9rem;
}

.table tbody tr:hover {
    background-color: #f8f9fa;
}

/* Card styles */
.card {
    background-color: #ffffff;
    border: 1px solid #e5e5e5;
    box-shadow: none;
    margin-bottom: 20px;
}

.card-header {
    background-color: #f8f9fa;
    border-bottom: 1px solid #e5e5e5;
    padding: 15px 20px;
}

.card-header h5 {
    margin: 0;
    font-size: 1.1rem;
    font-weight: 600;
    color: #1a1a1a;
}

.card-body {
    padding: 20px;
}

/* Button styles */
.btn {
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    padding: 10px 20px;
    font-size: 0.85rem;
    border: 2px solid transparent;
    transition: all 0.2s ease;
}

.btn-primary {
    background-color: #000000;
    border-color: #000000;
    color: #ffffff;
}

.btn-primary:hover {
    background-color: #333333;
    border-color: #333333;
}

.btn-success {
    background-color: #28a745;
    border-color: #28a745;
    color: #ffffff;
}

.btn-success:hover {
    background-color: #218838;
    border-color: #218838;
}

.btn-danger {
    background-color: #dc3545;
    border-color: #dc3545;
    color: #ffffff;
}

.btn-danger:hover {
    background-color: #c82333;
    border-color: #c82333;
}

.btn-warning {
    background-color: #ffc107;
    border-color: #ffc107;
    color: #000000;
}

.btn-warning:hover {
    background-color: #e0a800;
    border-color: #e0a800;
}

.btn-outline-primary {
    background-color: transparent;
    border-color: #000000;
    color: #000000;
}

.btn-outline-primary:hover {
    background-color: #000000;
    border-color: #000000;
    color: #ffffff;
}

.btn-sm {
    padding: 6px 12px;
    font-size: 0.75rem;
}

.btn-circle {
    width: 32px;
    height: 32px;
    text-align: center;
    padding: 0;
    font-size: 12px;
    line-height: 30px;
}

/* Drag and drop styles */
.sortable-ghost {
    opacity: 0.4;
}

.sortable-chosen {
    cursor: grabbing;
}

.drag-handle {
    cursor: grab;
    color: #6c757d;
}

.drag-handle:hover {
    color: #007bff;
}

/* Layout management */
.layout-section {
    border: 2px dashed #dee2e6;
    border-radius: 0.375rem;
    padding: 1rem;
    margin-bottom: 1rem;
    transition: all 0.3s ease;
}

.layout-section:hover {
    border-color: #007bff;
    background-color: rgba(0, 123, 255, 0.05);
}

.layout-section.active {
    border-color: #007bff;
    background-color: rgba(0, 123, 255, 0.1);
}

.section-controls {
    opacity: 0;
    transition: opacity 0.3s ease;
}

.layout-section:hover .section-controls {
    opacity: 1;
}

/* Media library */
.media-item {
    position: relative;
    border-radius: 0.375rem;
    overflow: hidden;
    transition: transform 0.2s ease;
}

.media-item:hover {
    transform: scale(1.05);
}

.media-item .media-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.7);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.media-item:hover .media-overlay {
    opacity: 1;
}

/* File upload */
.upload-area {
    border: 3px dashed #dee2e6;
    border-radius: 0.5rem;
    padding: 3rem;
    text-align: center;
    transition: all 0.3s ease;
    cursor: pointer;
}

.upload-area:hover,
.upload-area.dragover {
    border-color: #007bff;
    background-color: rgba(0, 123, 255, 0.05);
}

.upload-area.dragover {
    transform: scale(1.02);
}

/* Progress bars */
.progress {
    height: 0.5rem;
}

.progress-bar {
    transition: width 0.6s ease;
}

/* Alerts and notifications */
.alert {
    border: 0;
    border-radius: 0.5rem;
}

.alert-dismissible .btn-close {
    padding: 0.5rem 0.75rem;
}

/* Loading states */
.loading {
    opacity: 0.6;
    pointer-events: none;
}

.spinner-border-sm {
    width: 1rem;
    height: 1rem;
}

/* Dark mode adjustments */
[data-bs-theme="dark"] .sidebar .nav-link {
    color: #adb5bd;
}

[data-bs-theme="dark"] .sidebar .nav-link:hover {
    color: #0d6efd;
    background-color: rgba(13, 110, 253, 0.15);
}

[data-bs-theme="dark"] .sidebar .nav-link.active {
    color: #0d6efd;
    background-color: rgba(13, 110, 253, 0.2);
}

[data-bs-theme="dark"] .layout-section {
    border-color: #495057;
}

[data-bs-theme="dark"] .layout-section:hover {
    border-color: #0d6efd;
    background-color: rgba(13, 110, 253, 0.1);
}

/* Alerts and notifications */
.alert {
    border: none;
    padding: 15px 20px;
    margin-bottom: 20px;
    font-size: 0.9rem;
}

.alert-success {
    background-color: #d4edda;
    color: #155724;
    border-left: 4px solid #28a745;
}

.alert-danger {
    background-color: #f8d7da;
    color: #721c24;
    border-left: 4px solid #dc3545;
}

.alert-warning {
    background-color: #fff3cd;
    color: #856404;
    border-left: 4px solid #ffc107;
}

.alert-info {
    background-color: #cce7ff;
    color: #0c5460;
    border-left: 4px solid #17a2b8;
}

/* Data tables enhancements */
.dataTables_wrapper {
    padding-top: 20px;
}

.dataTables_filter input {
    border: 2px solid #e5e5e5;
    padding: 8px 12px;
    margin-left: 8px;
}

.dataTables_length select {
    border: 2px solid #e5e5e5;
    padding: 8px 12px;
}

.dataTables_info {
    font-size: 0.85rem;
    color: #666666;
}

.dataTables_paginate .paginate_button {
    padding: 8px 12px;
    margin: 0 2px;
    border: 2px solid #e5e5e5;
    background-color: #ffffff;
    color: #333333;
}

.dataTables_paginate .paginate_button:hover {
    background-color: #000000;
    color: #ffffff;
    border-color: #000000;
}

.dataTables_paginate .paginate_button.current {
    background-color: #000000;
    color: #ffffff;
    border-color: #000000;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .sidebar {
        transform: translateX(-100%);
        transition: transform 0.3s ease;
    }

    .sidebar.show {
        transform: translateX(0);
    }

    .main-content {
        margin-left: 0;
    }

    .content-header,
    .content-body {
        padding: 15px;
    }

    .stats-card .stats-number {
        font-size: 1.5rem;
    }
}

/* Print styles for admin */
@media print {
    .sidebar,
    .btn,
    .content-header {
        display: none !important;
    }

    .main-content {
        margin-left: 0 !important;
    }

    .content-body {
        padding: 0 !important;
    }
}

css/style.css.content:
/* Echhapa News Portal - Professional Design */

/* Base styles */
body {
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Arial', sans-serif;
    line-height: 1.6;
    color: #1a1a1a;
    background-color: #ffffff;
    margin: 0;
    padding: 0;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
    font-family: 'Georgia', 'Times New Roman', serif;
    font-weight: 700;
    line-height: 1.2;
    color: #000000;
    margin-bottom: 0.75rem;
}

.section-title {
    font-size: 1.25rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    border-bottom: 2px solid #000000;
    padding-bottom: 8px;
    margin-bottom: 20px;
    color: #000000;
}

/* Navigation */
.navbar {
    border-bottom: 1px solid #e5e5e5;
    background-color: #ffffff !important;
    padding: 0;
}

.navbar-brand {
    font-family: 'Georgia', 'Times New Roman', serif;
    font-weight: 900;
    letter-spacing: -1px;
    color: #000000 !important;
    font-size: 2rem;
}

.navbar-nav {
    border-top: 1px solid #e5e5e5;
    background-color: #f8f9fa;
    width: 100%;
}

.navbar-nav .nav-link {
    font-weight: 500;
    text-transform: uppercase;
    font-size: 0.85rem;
    letter-spacing: 0.5px;
    color: #333333 !important;
    padding: 12px 20px;
    border-right: 1px solid #e5e5e5;
    transition: all 0.2s ease;
}

.navbar-nav .nav-link:hover,
.navbar-nav .nav-link:focus {
    background-color: #000000;
    color: #ffffff !important;
}

/* Breaking news bar */
.breaking-news {
    background-color: #000000 !important;
    color: #ffffff;
    padding: 8px 0;
    font-weight: 600;
    text-transform: uppercase;
    font-size: 0.85rem;
    letter-spacing: 0.5px;
}

.breaking-news .badge {
    background-color: #dc3545;
    color: #ffffff;
    font-weight: 700;
    margin-right: 10px;
}

/* Featured article */
.featured-article {
    border-bottom: 1px solid #e5e5e5;
    padding-bottom: 20px;
    margin-bottom: 30px;
}

.featured-article h1,
.featured-article h2,
.featured-article h3 {
    font-size: 2.5rem;
    font-weight: 700;
    line-height: 1.1;
    margin-bottom: 15px;
    color: #000000;
}

.featured-article h3 a {
    color: #000000;
    text-decoration: none;
}

.featured-article h3 a:hover {
    color: #666666;
}

.featured-article .lead {
    font-size: 1.1rem;
    color: #666666;
    line-height: 1.5;
    margin-bottom: 10px;
}

/* Article cards */
.article-card {
    border: none;
    border-bottom: 1px solid #e5e5e5;
    padding: 20px 0;
    background: transparent;
}

.article-card:last-child {
    border-bottom: none;
}

.article-card .card-title {
    font-size: 1.25rem;
    font-weight: 600;
    line-height: 1.3;
    margin-bottom: 10px;
}

.article-card .card-title a {
    color: #000000;
    text-decoration: none;
}

.article-card .card-title a:hover {
    color: #666666;
}

.article-card .card-text {
    color: #666666;
    font-size: 0.95rem;
    line-height: 1.5;
}

/* Remove all rounded corners */
.card,
.btn,
.form-control,
.alert,
.badge,
.modal-content,
.dropdown-menu {
    border-radius: 0 !important;
}

/* Sidebar */
.sidebar {
    background: #f8f9fa;
    border-left: 2px solid #000000;
    padding: 20px;
}

.sidebar-widget {
    border-bottom: 1px solid #e5e5e5;
    padding-bottom: 25px;
    margin-bottom: 25px;
}

.sidebar-widget:last-child {
    border-bottom: none;
    margin-bottom: 0;
}

.widget-title {
    font-size: 1rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: #000000;
    margin-bottom: 15px;
    border-bottom: 2px solid #000000;
    padding-bottom: 5px;
}

.widget ul {
    list-style: none;
    padding: 0;
    margin: 0;
}

.widget ul li {
    border-bottom: 1px solid #e5e5e5;
    padding: 8px 0;
}

.widget ul li:last-child {
    border-bottom: none;
}

.widget ul li a {
    color: #333333;
    text-decoration: none;
    font-size: 0.9rem;
    line-height: 1.4;
    display: block;
    transition: color 0.2s ease;
}

.widget ul li a:hover {
    color: #000000;
}

/* Footer */
footer {
    background-color: #000000 !important;
    color: #ffffff;
    border-top: 1px solid #333333;
    margin-top: 50px;
}

footer h6 {
    color: #ffffff;
    font-weight: 600;
    text-transform: uppercase;
    font-size: 0.85rem;
    letter-spacing: 0.5px;
    margin-bottom: 15px;
}

footer a {
    color: #cccccc;
    text-decoration: none;
    font-size: 0.9rem;
}

footer a:hover {
    color: #ffffff;
}

footer .social-links a {
    display: inline-block;
    width: 40px;
    height: 40px;
    line-height: 40px;
    text-align: center;
    background-color: #333333;
    color: #ffffff;
    margin-right: 10px;
    transition: background-color 0.2s ease;
}

footer .social-links a:hover {
    background-color: #555555;
}

/* Article metadata */
.article-meta {
    border-top: 1px solid #e5e5e5;
    border-bottom: 1px solid #e5e5e5;
    padding: 15px 0;
    margin: 20px 0;
    font-size: 0.85rem;
    color: #666666;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.article-meta .date,
.article-meta .author,
.article-meta .category {
    margin-right: 20px;
}

/* Article content */
.article-content {
    font-size: 1.1rem;
    line-height: 1.7;
    color: #1a1a1a;
}

.article-content p {
    margin-bottom: 1.5rem;
}

.article-content h3,
.article-content h4 {
    margin-top: 2rem;
    margin-bottom: 1rem;
    color: #000000;
}

.article-content img {
    max-width: 100%;
    height: auto;
    margin: 2rem 0;
    border: 1px solid #e5e5e5;
}

.article-content ul,
.article-content ol {
    margin-bottom: 1.5rem;
    padding-left: 1.5rem;
}

.article-content blockquote {
    border-left: 3px solid #000000;
    padding-left: 20px;
    margin: 2rem 0;
    font-style: italic;
    color: #666666;
}

/* Category badges */
.category-badge {
    background-color: #000000;
    color: #ffffff;
    padding: 4px 8px;
    font-size: 0.75rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    font-weight: 600;
}

/* Buttons */
.btn {
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    padding: 10px 20px;
    font-size: 0.85rem;
    border: 2px solid transparent;
    transition: all 0.2s ease;
}

.btn-primary {
    background-color: #000000;
    border-color: #000000;
    color: #ffffff;
}

.btn-primary:hover {
    background-color: #333333;
    border-color: #333333;
}

.btn-outline-primary {
    background-color: transparent;
    border-color: #000000;
    color: #000000;
}

.btn-outline-primary:hover {
    background-color: #000000;
    border-color: #000000;
    color: #ffffff;
}

/* Grid layout improvements */
.row {
    margin-left: 0;
    margin-right: 0;
}

.col-lg-8,
.col-lg-4,
.col-md-6,
.col-md-4 {
    padding-left: 15px;
    padding-right: 15px;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .featured-article h1,
    .featured-article h2,
    .featured-article h3 {
        font-size: 1.8rem;
    }

    .section-title {
        font-size: 1.1rem;
    }

    .navbar-nav .nav-link {
        border-right: none;
        border-bottom: 1px solid #e5e5e5;
    }

    .sidebar {
        margin-top: 2rem;
        border-left: none;
        border-top: 2px solid #000000;
        padding-top: 20px;
    }
}

/* Print styles */
@media print {
    .navbar, .sidebar, footer, .btn, .breaking-news {
        display: none !important;
    }

    .container-fluid {
        width: 100% !important;
        max-width: none !important;
    }

    body {
        font-size: 12pt;
        line-height: 1.4;
    }
}

